mapper.update:
UPDATE TB_PD_CT_DEPTH1
SET NAME = #{pdCt1Name}
  , USE_YN = #{useYNEnum}
WHERE PD_CT1_NO = #{pdCt1No};

UPDATE TB_PD_CT_DEPTH2
SET NAME = #{pdCt2Name}
  , PD_CT1_NO = #{pdCt1No}
WHERE PD_CT2_NO = #{pdCt2No};

UPDATE TB_PD_CT_DEPTH2
SET USE_YN = #{useYNEnum}
WHERE NAME = #{pdCt2Name};
